"use strict";const i=require("../common/vendor.js"),n=require("../api/index.js");exports.uploadImage=async function(e){try{i.index.showLoading({title:"上传中..."});const o=await n.uploadApi.uploadSingle(e);return i.index.hideLoading(),o.url}catch(o){throw i.index.hideLoading(),o}},exports.uploadImages=async function(e){try{i.index.showLoading({title:`上传中 0/${e.length}`});const o=[];for(let t=0;t<e.length;t++){i.index.showLoading({title:`上传中 ${t+1}/${e.length}`});const d=await n.uploadApi.uploadSingle(e[t]);o.push(d.url)}return i.index.hideLoading(),o}catch(o){throw i.index.hideLoading(),o}};
